'      if a_page is not null and a_page.get_size > 0 then',
'        doc_name := doc_name || '' ('';',
'        FOR i_page IN 0 .. a_page.get_size - 1 loop',
'          if i_page>0 then',
'            doc_name := doc_name || '','';',
'          else',
'            doc_page := a_page.get_number(i_page);',
'          end if;',
'          doc_name := doc_name || a_page.get_number(i_page);',
'        end loop;',
'        doc_name := doc_name || '')'';',
'      end if; ',
'      -- http://www.examplesite.com/pls/apex/f?p=123:20:::::P20_ITEM1,P20_ITEM2:Oranges,Apples',
