'        method => ''POST'', ',
'        body => APEX_UTIL.CLOB_TO_BLOB(jo.to_clob)                                                                                         ',
'    ); ',
'  json_response := DBMS_CLOUD.get_response_text(resp); ',
'  log( ''<ai_agent_session> response'', json_response);',
'  res := JSON_VALUE(json_response, ''$.id'');',
'  log( ''<ai_agent_session> res'', res);  ',
'  return res; ',
'end;',
'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION ai_agent_execute( agent_endpoint_id varchar2, agent_session_id varchar2, message varchar2 ) return CLOB is ',
