'}'');  ',
'  jo := treat(je AS JSON_OBJECT_T);',
'  jo.put(''userMessage'', message);',
'  log( ''<ai_agent_execute> request'', jo.to_clob());',
'  log( ''<ai_agent_execute> LENGTH'', ''LENGTH='' || LENGTH(jo.to_clob()));',
'  resp := DBMS_CLOUD.send_request( ',
'        credential_name => ''OCI$RESOURCE_PRINCIPAL'', ',
'        uri =>''https://agent-runtime.generativeai.'' || g_region || ''.oci.oraclecloud.com/20240531/agentEndpoints/''||agent_endpoint_id||''/sessions/''||agent_session_id||''/actions/execute'',',
'        method => ''POST'', ',
'        body => APEX_UTIL.CLOB_TO_BLOB(jo.to_clob)                                                                                         ',
'    ); ',
'  json_response := DBMS_CLOUD.get_response_text(resp); ',
'  log( ''<ai_agent_execute> response'', json_response);',
'  -- res := JSON_VALUE(json_response, ''$.message.content.text'');',
'  -- log( ''<ai_agent_execute> res'', res);  ',
'  return json_response; ',
'end;',
'',
'FUNCTION create_pre_authenticated_request( url varchar2, page number ) return varchar2 is',
'  resp DBMS_CLOUD_TYPES.resp;   ',
'  json_response CLOB; ',
'  s VARCHAR2(32767); ',
'  je JSON_ELEMENT_T;',
'  jo JSON_OBJECT_T;  ',
'',
'  res varchar2(4096);',
'  d varchar2(4096);',
'  url1 varchar2(4096);',
'  url2 varchar2(4096);',
'  pos_sep number;',
'begin',
'  pos_sep := instr( url, ''/o/'' );',
'  url1 := substr( url, 1, pos_sep-1 );',
'  url2 := substr( url, pos_sep+3 );',
'  dbms_output.put_line( url1 );',
'  dbms_output.put_line( url2 );',
'',
'  je := JSON_ELEMENT_T.parse(''',
'  { "accessType":"ObjectRead",',
'    "name":"par-object-20241118-1126",',
'    "timeExpires":"2024-11-25T10:26:34.017Z",',
'    "objectName":"xxxx.pdf"',
'  }'');  ',
'  d := to_char( systimestamp + 10.0/1440.0, ''YYYY-MM-DD"T"HH24:MI:SS"Z"'');',
'  jo := treat(je AS JSON_OBJECT_T);',
'  jo.put(''name'', ''apex'' || d);',
'  jo.put(''timeExpires'', d);',
'  jo.put(''objectName'', utl_url.unescape( url2, url_charset => ''UTF-8'' ));',
'  log( ''<create_pre_authenticated_request> request'', jo.to_clob());',
'  log( ''<create_pre_authenticated_request> LENGTH'', ''LENGTH='' || LENGTH(jo.to_clob()));',
'  resp := DBMS_CLOUD.send_request( ',
'        credential_name => ''OCI$RESOURCE_PRINCIPAL'', ',
'        uri =>url1  || ''/p/'',',
'        method => ''POST'', ',
'        body => APEX_UTIL.CLOB_TO_BLOB(jo.to_clob)                                                                                         ',
'    ); ',
'  json_response := DBMS_CLOUD.get_response_text(resp); ',
'  log( ''<create_pre_authenticated_request> response'', json_response);',
'  res := JSON_VALUE(json_response, ''$.fullPath'');',
'  if page is not null then',
'    res := res || ''#page='' || page;',
'  end if;',
'  return res;',
'end;',
'',
